const generateRandomAnimation = () => {
  const randomValuesX = Array.from({ length: 5 }, () => Math.random() * 10 - 5);
  const randomValuesY = Array.from({ length: 5 }, () => Math.random() * 10 - 5);

  const keyframes = [
    'translate(0px, 0px)',
    ...randomValuesX.map((x, i) => `translate(${x}px, ${randomValuesY[i]}px)`),
    'translate(0px, 0px)',
  ];

  return {
    transform: keyframes,
    transition: {
      duration: 5,
      ease: 'easeInOut',
      repeat: Infinity,
      repeatType: 'loop',
    },
  };
};

