const useDynamicSpring = (mouseX, mouseY, gRef, svgRef) => {
  const motionX = useMotionValue(0);
  const motionY = useMotionValue(0);

  const [damping, setDamping] = useState(35);
  const [stiffness, setStiffness] = useState(130);
  const [isActive, setIsActive] = useState(false);

  useEffect(
    () => {
      if (!gRef.current || !svgRef.current) return;

      const svgRect = svgRef.current.getBoundingClientRect();
      const gRect = gRef.current.getBoundingClientRect();

      const gCenterX = gRect.left + gRect.width / 2 - svgRect.left;
      const gCenterY = gRect.top + gRect.height / 2 - svgRect.top;

      const activeRadiusX = svgRect.width * 0.1;
      const activeRadiusY = svgRect.height * 0.15;

      const distanceX = Math.abs(mouseX - gCenterX);
      const distanceY = Math.abs(mouseY - gCenterY);

      const newDamping = Math.max(35, 120 - distanceX * 2);
      const newStiffness = Math.min(130, 120 + distanceX * 2);

      setDamping(newDamping);
      setStiffness(newStiffness);

      const isCurrentlyActive = distanceX < activeRadiusX && distanceY < activeRadiusY;
      setIsActive(isCurrentlyActive);

      if (isCurrentlyActive) {
        motionX.set(mouseX - gCenterX);
        motionY.set(mouseY - gCenterY);
      } else {
        motionX.set(0);
        motionY.set(0);
      }
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [mouseX, mouseY, gRef, svgRef]
  );

  const springConfig = { damping, stiffness };
  const springX = useSpring(motionX, springConfig);
  const springY = useSpring(motionY, springConfig);

  return { x: springX, y: springY, isActive };
};

